mapper.update:
UPDATE product
prod_name = #{prodName} , prod_detail = #{prodDetail} , manufacture_day= TO_DATE(#{manuDate},'YYYY-MM-DD') , price = #{price} , image_file = #{fileName}
WHERE prod_no = #{prodNo}

mapper.select:
SELECT * FROM ( SELECT inner_table.* , ROWNUM AS row_seq FROM ( SELECT * FROM product
prod_no LIKE LIKE '%' || #{searchKeyword} || '%'
prod_name LIKE '%' || #{searchKeyword} || '%'
price = #{searchKeyword}
ORDER BY prod_no ) inner_table WHERE ROWNUM <= #{endRowNum} ) WHERE row_seq BETWEEN #{startRowNum} AND #{endRowNum}

SELECT COUNT(*) FROM( SELECT * FROM product
prod_no LIKE '%' || #{searchKeyword} || '%'
prod_name LIKE '%' || #{searchKeyword} || '%'
price = #{searchKeyword}
) countTable
